(76, 1, '2023-04-08 00:56:58', '2023-04-07 21:56:58', '<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":\"40%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:40%\"><!-- wp:image {\"id\":249,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost/usl/wp-content/uploads/2023/04/istockphoto-157581637-612x612-6.jpg\" alt=\"\" class=\"wp-image-249\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph -->\n<p>Поточний та капітальний ремонт ліфто</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->', 'Поточний та капітальний ремонт ліфтів', '', 'publish', 'closed', 'closed', '', '%d0%bf%d0%be%d1%82%d0%be%d1%87%d0%bd%d0%b8%d0%b9-%d1%82%d0%b0-%d0%ba%d0%b0%d0%bf%d1%96%d1%82%d0%b0%d0%bb%d1%8c%d0%bd%d0%b8%d0%b9-%d1%80%d0%b5%d0%bc%d0%be%d0%bd%d1%82-%d0%bb%d1%96%d1%84%d1%82%d1%96', '', '', '2023-04-08 12:29:09', '2023-04-08 09:29:09', '', 54, 'http://localhost/usl/?page_id=76', 0, 'page', '', 0),
